import React, { useState, useEffect } from 'react'
import Layout from '../components/layout'
import Style from '../styles/pages/menu/menu.module.css'

export default function Menu() {
    const [data, setData] = useState<menuData[]>(payloadMenuData)
    const [menuType, setMenuType] = useState<menuType[]>(payloadMenuType)
    // useEffect(() => {
    //     setMenuType(payloadMenuType)
    // }, [data, menuType])
    return (
        <Layout>
            <div className={Style.mobileContainer}>
                <div >
                    <h1>Menu</h1>
                </div>
                <select>
                    <option>All</option>
                    {menuType.map(item => (
                        <option>{item.name}</option>
                    ))}
                </select>
                <div className={Style.menuList}>
                    {data.map(item => (
                        <div className={Style.menu}>
                            <div className={Style.menuImage}>
                                {/* <img src="https://images.unsplash.com/photo-1485808191679-5f86510681a2?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=934&q=80"/> */}
                            </div>
                            <div className={Style.menuName}>
                                <p>{item.name}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </Layout>
    )
}

interface menuData {
    "_id": string,
    "name": string,
    "detail": string,
    "image": string,
}

interface menuType {
    "_id": string,
    "name": string,
}

const payloadMenuData: menuData[] = [
    {
        "_id": "1",
        "name": "Capucino",
        "detail": "coffee with milk and milk foam",
        "image": "capucino.jpeg",
    },
    {
        "_id": "2",
        "name": "Latte",
        "detail": "coffee with milk",
        "image": "latte.jpeg",
    },
    {
        "_id": "3",
        "name": "Expresso",
        "detail": "pure shot of coffee",
        "image": "expresso.jpeg",
    }
]

const payloadMenuType: menuType[] = [
    {
        "_id": "1",
        "name": "Coffee",
    },
    {
        "_id": "2",
        "name": "Bakery",
    }
]
