import Layout from '../components/layout'
import GoogleMapReact from 'google-map-react'
import {useRouter} from 'next/router'

import Style from '../styles/pages/contact/contact.module.css'

const defaultProps = {
    center: {
        lat: 13.734114,
        lng: 100.528321,
    },
    zoom: 17,
}

const Marker = props => {
    // return <img style={{height: "22px", position:"relative", right:"22px"}} src = "https://www.flaticon.com/svg/static/icons/svg/2991/2991231.svg" />
    return <div ><img style={{width:"3rem", height:"3rem", position:"absolute", transform:"translate(-50%, -50%)"}} src={props.img_src} /></div>
}


// const imageSource = "https://www.flaticon.com/svg/static/icons/svg/2991/2991231.svg"
const imageSource = "https://www.flaticon.com/svg/static/icons/svg/924/924514.svg"

export default function Contact() {
    const {locale} = useRouter()
    return (
        <Layout>
            <div>
                <h1>Contact</h1>
                <div>
                    <div>
                        <h1>Homgroon Shop</h1>
                        <h1>214/17 Siam Paragon Bangkok</h1>
                    </div>
                    <h1>Contact: 081-4219084</h1>
                    {/* <div></div> */}
                    <div style={{height: "50vh", width: "100%"}}>
                        <GoogleMapReact
                            bootstrapURLKeys = {{
                                language: locale,
                                region: "th",
                                libraries: ["visualization", "places"],
                                // key:"AIzaSyDlkP9JL19YsHZYhW9PWAOv4Zuo3s3jZ38",
                            }}
                            defaultCenter = {defaultProps.center}
                            defaultZoom = {defaultProps.zoom}
                        >
                            <Marker lat={defaultProps.center.lat} lng={defaultProps.center.lng} img_src={imageSource} />
                            {/* <Marker lat={defaultProps.center.lat} lng={defaultProps.center.lng} text="My mark" /> */}
                        </GoogleMapReact>
                    </div>
                </div>
            </div>

        </Layout>
    )
}
