import React, { useState, useEffect } from 'react'
import Layout from '../components/layout'
import Style from '../styles/pages/menu/menu.module.css'

export default function Menu() {
    const [data, setData] = useState<menuData[]>(payloadMenuData)
    const [menuType, setMenuType] = useState<menuType[]>([{ "_id": "0", "name": "none" }])
    useEffect(() => {
        setMenuType(payloadMenuType)
    }, [data, menuType])
    return (
        <Layout>
            <div>
                <h1>Menu page</h1>
            </div>
            <div>
                <select>
                    <option>All</option>
                {menuType.map(item => (
                    <option>{item.name}</option>
                ))}
                </select>
            </div>
            <div>
                {data.map(item => (
                    <div>

                    </div>
                ))}
            </div>

        </Layout>
    )
}

interface menuData {
    "_id": string,
    "name": string,
    "detail": string,
    "image": string,
}

interface menuType {
    "_id": string,
    "name": string,
}

const payloadMenuData: menuData[] = [
    {
        "_id": "1",
        "name": "Capucino",
        "detail": "coffee with milk and milk foam",
        "image": "capucino.jpeg",
    },
    {
        "_id": "2",
        "name": "Latte",
        "detail": "coffee with milk",
        "image": "latte.jpeg",
    },
    {
        "_id": "3",
        "name": "Expresso",
        "detail": "pure shot of coffee",
        "image": "expresso.jpeg",
    }
]

const payloadMenuType: menuType[] = [
    {
        "_id": "1",
        "name": "Coffee",
    },
    {
        "_id": "2",
        "name": "Bakery",
    }
]
